mapper.sql:
select

from art_dummy where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	d.id 
	, d.name 
	, d.dummy_status 
	, d.description 
	, d.birthday 
	, d.start_time 
	, d.age 
	, d.balance 
	, d.facctor 
	, d.delete_status 
	, d.trace_code_i 
	, d.trace_code_u 
	, d.host_name 
	, d.create_user_id 
	, d.create_by 
	, d.create_date 
	, d.update_user_id 
	, d.update_by 
	, d.update_date 
from art_dummy d 
where d.delete_status = 1

	and d.id = #{id}


	and d.name like concat('%', #{name}, '%')


	and d.dummy_status = #{dummy_status}


	and d.description like concat('%', #{description}, '%')


	and date_format(d.birthday, '%Y-%m-%d') >= #{birthday_from}


	and date_format(d.birthday, '%Y-%m-%d') <= #{birthday_to}


	and date_format(d.birthday, '%Y-%m-%d') = #{birthday}


	and date_format(d.start_time, '%Y-%m-%d') >= #{start_time_from}


	and date_format(d.start_time, '%Y-%m-%d') <= #{start_time_to}


	and date_format(d.start_time, '%Y-%m-%d') = #{start_time}


	and d.age = #{age}


	and d.balance = #{balance}


	and d.facctor = #{facctor}


	and d.trace_code_i like concat('%', #{trace_code_i}, '%')


	and d.trace_code_u like concat('%', #{trace_code_u}, '%')


	and d.host_name like concat('%', #{host_name}, '%')


	and d.create_user_id = #{create_user_id}


	and d.create_by like concat('%', #{create_by}, '%')


	and date_format(d.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(d.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(d.create_date, '%Y-%m-%d') = #{create_date}


	and d.update_user_id = #{update_user_id}


	and d.update_by like concat('%', #{update_by}, '%')


	and date_format(d.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(d.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(d.update_date, '%Y-%m-%d') = #{update_date}

order by d.id desc

mapper.insert:
insert into art_dummy 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_dummy where 1



	and ${key} is null


	and ${key} = #{value}
