mapper.sql:
select

from art_user_action_log where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select ual.id , ual.user_id , ual.action_code , ual.message , ual.mile_stone , ual.session_code , ual.thread_code , ual.page_code , ual.browser , ual.remote_ip , ual.app_name , ual.trace_code , ual.create_user_id , ual.create_by , ual.create_date , ual.update_user_id , ual.update_by , ual.update_date from art_user_action_log ual where 1 = 1
and ual.id = #{id}
and ual.user_id = #{user_id}
and ual.action_code like concat('%', #{action_code}, '%')
and ual.message like concat('%', #{message}, '%')
and ual.mile_stone = #{mile_stone}
and ual.session_code like concat('%', #{session_code}, '%')
and ual.thread_code like concat('%', #{thread_code}, '%')
and ual.page_code like concat('%', #{page_code}, '%')
and ual.browser like concat('%', #{browser}, '%')
and ual.remote_ip like concat('%', #{remote_ip}, '%')
and ual.app_name like concat('%', #{app_name}, '%')
and ual.trace_code like concat('%', #{trace_code}, '%')
and ual.create_user_id = #{create_user_id}
and ual.create_by like concat('%', #{create_by}, '%')
and date_format(ual.create_date, '%Y-%m-%d') >= #{create_date_from}
and date_format(ual.create_date, '%Y-%m-%d') <= #{create_date_to}
and date_format(ual.create_date, '%Y-%m-%d') = #{create_date}
and ual.update_user_id = #{update_user_id}
and ual.update_by like concat('%', #{update_by}, '%')
and date_format(ual.update_date, '%Y-%m-%d') >= #{update_date_from}
and date_format(ual.update_date, '%Y-%m-%d') <= #{update_date_to}
and date_format(ual.update_date, '%Y-%m-%d') = #{update_date}
order by ual.id desc

mapper.insert:
insert into art_user_action_log 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_user_action_log where 1



	and ${key} is null


	and ${key} = #{value}
